import { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';

const Nav = styled.nav`
  padding: 1rem 2rem;
  background: ${props => props.isDark ? 'rgba(18, 18, 18, 0.95)' : 'rgba(255, 255, 255, 0.95)'};
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 100;
  box-shadow: ${props => props.isDark ? '0 2px 20px rgba(0, 0, 0, 0.3)' : '0 2px 20px rgba(0, 0, 0, 0.1)'};
  transition: all 0.3s ease;
`;

const NavContainer = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 40px;
`;

const Logo = styled(Link)`
  font-size: 24px;
  color: #FF6B35;
  text-decoration: none;
  cursor: pointer;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }

  &:active {
    transform: scale(0.95);
  }
`;

const NavLinks = styled.div`
  display: flex;
  gap: 0.8rem;
  align-items: center;
  margin-right: 10px;
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: ${props => props.isDark ? '#ffffff' : '#333'};
  font-weight: 500;
  font-size: 16px;

  &:hover {
    color: #FF6B35;
  }
`;

const DropdownContainer = styled.div`
  position: relative;
  display: inline-block;

  &:hover > div {
    display: block;
  }
`;

const DropdownContent = styled.div`
  display: none;
  position: absolute;
  top: 100%;
  left: 50%;
  transform: translateX(-50%);
  background: ${props => props.isDark ? '#121212' : 'white'};
  min-width: 160px;
  box-shadow: ${props => props.isDark ? '0 8px 16px rgba(0, 0, 0, 0.3)' : '0 8px 16px rgba(255, 107, 53, 0.1)'};
  border-radius: 8px;
  padding: 0.5rem 0;
  z-index: 1;
`;

const DropdownLink = styled(Link)`
  text-decoration: none;
  color: ${props => props.isDark ? '#ffffff' : '#333'};
  padding: 12px 20px;
  display: block;
  font-weight: 500;

  &:hover {
    background: ${props => props.isDark ? '#1f1f1f' : '#FFF5EC'};
    color: #FF6B35;
  }
`;

const ThemeToggle = styled.button`
  background: none;
  border: 2px solid #FF6B35;
  color: #FF6B35;
  width: 35px;
  height: 35px;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-left: 10px;
  transition: all 0.3s ease;

  &:hover {
    background: #FF6B35;
    color: ${props => props.isDark ? '#121212' : '#ffffff'};
  }
`;

const ContactButton = styled(Link)`
  background: #FF6B35;
  color: white;
  padding: 10px 20px;
  border-radius: 25px;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    background: #FF8B55;
    transform: translateY(-2px);
  }
`;

function Navbar({ isDark, setIsDark }) {
  return (
    <Nav isDark={isDark}>
      <NavContainer>
        <Logo to="/">ThruMyLens</Logo>
        <NavLinks>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/work">Work</NavLink>
          <NavLink to="/about">About</NavLink>
          <NavLink to="/blog">Blog</NavLink>
          <NavLink to="/reviews">Reviews</NavLink>
          <NavLink to="/clients">Clients</NavLink>
          <NavLink to="/contact">Contact</NavLink>
          <ThemeToggle onClick={() => setIsDark(!isDark)}>
            {isDark ? '‚òÄÔ∏è' : 'üåô'}
          </ThemeToggle>
        </NavLinks>
      </NavContainer>
    </Nav>
  );
}

export default Navbar;
