import { useParams, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { photographerWorks } from './Home';

const PortfolioSection = styled.section`
  padding: 80px 20px;
  background: ${props => props.isDark ? '#2A1F1D' : '#fff'};
`;

const PortfolioHeader = styled.div`
  text-align: center;
  margin-bottom: 50px;

  h1 {
    color: #FF6B35;
    font-size: 36px;
    margin-bottom: 20px;
  }
`;

const PortfolioGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const WorkCard = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 4px 16px rgba(255, 107, 53, 0.1);

  img {
    width: 100%;
    height: 300px;
    object-fit: cover;
  }

  .content {
    padding: 20px;
    background: ${props => props.isDark ? '#3D2E2A' : '#FFF5EC'};

    h3 {
      color: #FF6B35;
      margin-bottom: 10px;
    }

    p {
      color: ${props => props.isDark ? '#FFE6D9' : '#666'};
    }
  }
`;

const BackButton = styled(motion.button)`
  background: #FF6B35;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 25px;
  cursor: pointer;
  margin-bottom: 30px;
  font-size: 16px;

  &:hover {
    background: #FF8B55;
  }
`;

function PhotographerPortfolio({ isDark }) {
  const { name } = useParams();
  const navigate = useNavigate();
  const decodedName = decodeURIComponent(name);

  const works = photographerWorks[decodedName] || [];

  return (
    <PortfolioSection isDark={isDark}>
      <PortfolioHeader>
        <BackButton
          onClick={() => navigate('/')}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          ‚Üê Back to Team
        </BackButton>
        <h1>{decodedName}'s Portfolio</h1>
      </PortfolioHeader>

      <PortfolioGrid>
        <AnimatePresence>
          {works.map((work) => (
            <WorkCard
              key={work.id}
              isDark={isDark}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              <img src={work.image} alt={work.title} />
              <div className="content">
                <h3>{work.title}</h3>
                <p>{work.description}</p>
              </div>
            </WorkCard>
          ))}
        </AnimatePresence>
      </PortfolioGrid>
    </PortfolioSection>
  );
}

export default PhotographerPortfolio;
