import { motion, AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => props.isDark ? '#2A1F1D' : '#FFF5EC'};
`;

const HeroContent = styled(motion.div)`
  text-align: center;
`;

const HeroTitle = styled.h1`
  color: #FF6B35;
  font-size: 48px;
  margin-bottom: 20px;
`;

const HeroSubtitle = styled.p`
  color: ${props => props.isDark ? '#FFE6D9' : '#666'};
  font-size: 24px;
  margin-bottom: 30px;
`;

const JourneyButton = styled(Link)`
  background: #FF6B35;
  color: white;
  padding: 15px 30px;
  border-radius: 25px;
  text-decoration: none;
  font-weight: 500;
  transition: background 0.3s;

  &:hover {
    background: #FF8B55;
  }
`;

const FeaturedWorks = styled.section`
  padding: 80px 20px;
  background: ${props => props.isDark ? '#2A1F1D' : '#FFF5EC'};
  position: relative;

  h2 {
    text-align: center;
    color: #FF6B35;
    font-size: 36px;
    margin-bottom: 50px;
  }
`;

const FeaturedGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const FeaturedCard = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  height: 300px;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  &:hover ${CardOverlay} {
    transform: translateY(0);
  }
`;

// First, remove the photographerWorks export since it's not needed in this file

// Move CardOverlay definition before FeaturedCard
const CardOverlay = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 20px;
  transform: translateY(100%);
  transition: transform 0.3s;

  h3 {
    font-size: 20px;
    margin-bottom: 10px;
  }

  .details {
    font-size: 14px;
    margin: 10px 0;
  }

  span {
    display: inline-block;
    background: #FF6B35;
    padding: 5px 10px;
    border-radius: 15px;
    font-size: 12px;
  }
`;

const ArrowButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: #FF6B35;
  color: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  font-size: 20px;

  &.prev {
    left: 20px;
  }

  &.next {
    right: 20px;
  }

  &:hover {
    background: #FF8B55;
  }
`;

const ReviewsSection = styled.section`
  padding: 80px 20px;
  background: ${props => props.isDark ? '#2A1F1D' : '#FFF5EC'};
  text-align: center;

  h2 {
    color: #FF6B35;
    font-size: 36px;
    margin-bottom: 50px;
  }
`;

const ReviewCard = styled(motion.div)`
  max-width: 800px;
  margin: 0 auto;
  padding: 30px;
  background: ${props => props.isDark ? '#3D2E2A' : '#fff'};
  border-radius: 15px;
  box-shadow: 0 4px 16px rgba(255, 107, 53, 0.1);

  p {
    color: ${props => props.isDark ? '#FFE6D9' : '#666'};
    font-size: 18px;
    line-height: 1.6;
    margin-bottom: 20px;
  }

  h4 {
    color: #FF6B35;
    margin-bottom: 5px;
  }

  span {
    color: ${props => props.isDark ? '#FFE6D9' : '#666'};
    font-size: 14px;
  }
`;

const StatsSection = styled(motion.section)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 30px;
  padding: 60px 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const StatBox = styled(motion.div)`
  background: ${props => props.bgColor};
  padding: 30px;
  border-radius: 15px;
  text-align: center;
  color: white;
`;

const StatNumber = styled.h3`
  font-size: 36px;
  margin-bottom: 10px;
`;

const StatLabel = styled.p`
  font-size: 18px;
`;

const featuredWorks = [
  {
    id: 1,
    title: "Urban Landscapes",
    description: "Modern architectural photography series",
    category: "Architecture",
    photographer: "Sofia Garcia",
    location: "Downtown Manhattan",
    date: "August 15, 2023",
    image: "https://images.unsplash.com/photo-1449157291145-7efd050a4d0e"
  },
  // ... other featured works
];

const reviews = [
  {
    id: 1,
    text: "Emma captured our wedding day perfectly. Every photo tells a story and brings back beautiful memories.",
    name: "Sarah & Michael",
    event: "Wedding Photography"
  },
  // ... other reviews
];

function Home({ isDark }) {
  const [currentReview, setCurrentReview] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 3;
  const pageCount = Math.ceil(featuredWorks.length / itemsPerPage);

  const prevPage = () => {
    setCurrentPage((prev) => (prev === 0 ? pageCount - 1 : prev - 1));
  };

  const nextPage = () => {
    setCurrentPage((prev) => (prev === pageCount - 1 ? 0 : prev + 1));
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentReview((prev) => (prev === reviews.length - 1 ? 0 : prev + 1));
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <>
      <HeroSection isDark={isDark}>
        <HeroContent
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <HeroTitle>Capturing Life's Beautiful Moments</HeroTitle>
          <HeroSubtitle isDark={isDark}>Professional Photography Services</HeroSubtitle>
          <JourneyButton to="/about">Explore More</JourneyButton>
        </HeroContent>
      </HeroSection>

      <FeaturedWorks isDark={isDark}>
        <h2>Featured Works</h2>
        <ArrowButton className="prev" onClick={prevPage}>&larr;</ArrowButton>
        <ArrowButton className="next" onClick={nextPage}>&rarr;</ArrowButton>
        <FeaturedGrid>
          <AnimatePresence mode='wait'>
            {featuredWorks
              .slice(currentPage * itemsPerPage, (currentPage + 1) * itemsPerPage)
              .map((work) => (
                <FeaturedCard
                  key={work.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  whileHover={{ y: -5 }}
                  transition={{ duration: 0.3 }}
                >
                  <img src={work.image} alt={work.title} />
                  <CardOverlay>
                    <h3>{work.title}</h3>
                    <p>{work.description}</p>
                    <div className="details">
                      <p>Photographer: {work.photographer}</p>
                      <p>Location: {work.location}</p>
                      <p>Date: {work.date}</p>
                    </div>
                    <span>{work.category}</span>
                  </CardOverlay>
                </FeaturedCard>
            ))}
          </AnimatePresence>
        </FeaturedGrid>
      </FeaturedWorks>

      <ReviewsSection isDark={isDark}>
        <h2>Client Reviews</h2>
        <AnimatePresence mode="wait">
          <ReviewCard
            key={reviews[currentReview].id}
            isDark={isDark}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            <p>"{reviews[currentReview].text}"</p>
            <h4>{reviews[currentReview].name}</h4>
            <span>{reviews[currentReview].event}</span>
          </ReviewCard>
        </AnimatePresence>
      </ReviewsSection>

      <StatsSection
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <StatBox 
          bgColor="#FF6B35"
          whileHover={{ scale: 1.05, translateY: -5 }}
          transition={{ duration: 0.3 }}
        >
          <StatNumber>15+</StatNumber>
          <StatLabel>Years of Experience</StatLabel>
        </StatBox>
        <StatBox 
          bgColor="#FF8B55"
          whileHover={{ scale: 1.05, translateY: -5 }}
          transition={{ duration: 0.3 }}
        >
          <StatNumber>105+</StatNumber>
          <StatLabel>Projects Completed</StatLabel>
        </StatBox>
        <StatBox 
          bgColor="#FFA675"
          whileHover={{ scale: 1.05, translateY: -5 }}
          transition={{ duration: 0.3 }}
        >
          <StatNumber>7</StatNumber>
          <StatLabel>Awards Received</StatLabel>
        </StatBox>
      </StatsSection>
    </>
  );
}

export default Home;
