import { useParams, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import styled from '@emotion/styled';

const PortfolioSection = styled.section`
  padding: 80px 20px;
  background: ${props => props.isDark ? '#2A1F1D' : '#fff'};
`;

const PortfolioHeader = styled.div`
  text-align: center;
  margin-bottom: 50px;

  h1 {
    color: #FF6B35;
    font-size: 36px;
    margin-bottom: 20px;
  }
`;

const PortfolioGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const WorkCard = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 4px 16px rgba(255, 107, 53, 0.1);

  img {
    width: 100%;
    height: 300px;
    object-fit: cover;
  }

  .content {
    padding: 20px;
    background: ${props => props.isDark ? '#3D2E2A' : '#FFF5EC'};

    h3 {
      color: #FF6B35;
      margin-bottom: 10px;
    }

    p {
      color: ${props => props.isDark ? '#FFE6D9' : '#666'};
    }
  }
`;

const BackButton = styled(motion.button)`
  background: #FF6B35;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 25px;
  cursor: pointer;
  margin-bottom: 30px;
  font-size: 16px;

  &:hover {
    background: #FF8B55;
  }
`;

function PhotographerPortfolio({ isDark }) {
  const { name } = useParams();
  const navigate = useNavigate();
  const decodedName = decodeURIComponent(name);

  const photographerWorks = {
    "Emma Thompson": [
      {
        id: "w1",
        title: "Beachside Ceremony",
        image: "https://images.unsplash.com/photo-1519741497674-611481863552",
        description: "Intimate beach wedding ceremony at sunset"
      },
      {
        id: "w2",
        title: "Garden Reception",
        image: "https://images.unsplash.com/photo-1519225421980-715cb0215aed",
        description: "Elegant outdoor wedding reception"
      },
      {
        id: "w3",
        title: "First Dance",
        image: "https://images.unsplash.com/photo-1583939003579-730e3918a45a",
        description: "Capturing the magical first dance moment"
      }
    ],
    "David Chen": [
      {
        id: "n1",
        title: "Mountain Dawn",
        image: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4",
        description: "Sunrise over the mountain peaks"
      },
      {
        id: "n2",
        title: "Forest Mist",
        image: "https://images.unsplash.com/photo-1511497584788-876760111969",
        description: "Mystical forest in morning fog"
      }
    ],
    "Marcus Williams": [
      {
        id: "e1",
        title: "Tech Conference 2023",
        image: "https://images.unsplash.com/photo-1511578314322-379afb476865",
        description: "Annual technology summit keynote presentation"
      }
    ],
    "Sofia Garcia": [
      {
        id: "p1",
        title: "Urban Portrait Series",
        image: "https://images.unsplash.com/photo-1534528741775-53994a69daeb",
        description: "Contemporary portrait in urban setting"
      }
    ],
    "James Morrison": [
      {
        id: "a1",
        title: "Alpine Adventure",
        image: "https://images.unsplash.com/photo-1464822759023-fed622ff2c3b",
        description: "Mountain climbing expedition at dawn"
      }
    ],
    "Isabella Laurent": [
      {
        id: "f1",
        title: "Haute Couture",
        image: "https://images.unsplash.com/photo-1509631179647-0177331693ae",
        description: "Paris Fashion Week editorial shoot"
      }
    ]
  };

  const works = photographerWorks[decodedName] || [];

  return (
    <PortfolioSection isDark={isDark}>
      <PortfolioHeader>
        <BackButton
          onClick={() => navigate('/')}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          ← Back to Team
        </BackButton>
        <h1>{decodedName}'s Portfolio</h1>
      </PortfolioHeader>

      <PortfolioGrid>
        <AnimatePresence>
          {works.map((work) => (
            <WorkCard
              key={work.id}
              isDark={isDark}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              <img src={work.image} alt={work.title} />
              <div className="content">
                <h3>{work.title}</h3>
                <p>{work.description}</p>
              </div>
            </WorkCard>
          ))}
        </AnimatePresence>
      </PortfolioGrid>
    </PortfolioSection>
  );
}

export default PhotographerPortfolio;
