import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from '@emotion/styled';
import { photographerWorks } from './home';

const PortfolioSection = styled.section`
  padding: 80px 20px;
  background: ${props => props.isDark ? '#2A1F1D' : '#fff'};
`;

const PortfolioGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const WorkCard = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 4px 16px rgba(255, 107, 53, 0.1);

  img {
    width: 100%;
    height: 300px;
    object-fit: cover;
  }
`;

function PhotographerPortfolio({ isDark }) {
  const { name } = useParams();
  const works = photographerWorks[decodeURIComponent(name)] || [];

  return (
    <PortfolioSection isDark={isDark}>
      <h1>{decodeURIComponent(name)}'s Portfolio</h1>
      <PortfolioGrid>
        {works.map((work) => (
          <WorkCard
            key={work.id}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <img src={work.image} alt={work.title} />
            <h3>{work.title}</h3>
            <p>{work.description}</p>
          </WorkCard>
        ))}
      </PortfolioGrid>
    </PortfolioSection>
  );
}

export default PhotographerPortfolio;
